'use strict';

const _ = require('lodash');
const utils = require('../utils');
const request = require('request');

module.exports = function(options) {
	options = options || {};
	var logger = utils.getLogger('couchdb');
	const BASE_URL = options.baseUrl || 'http://localhost:5984/passbookmanager';

	var defaultOptions = {
		method: 'GET',
		headers: {
			//'Accept': 'application/json',
			'Content-Type': 'application/json',
			'x-token': 'my-token'
		}
	};

	var baseRequest = request.defaults(defaultOptions);

	var sendRequest = function(options) {
		options.url = `${BASE_URL}/${options.url}`;

		logger('sendRequest', options);
		let _response = {};
		return new Promise(function(resolve, reject) {
			_.defer(function() {
				request(options, function(err, resp, body) {
					if (err) {
						reject(err);
					}
					logger('response', body);
					_response.data = body;

					if (!options.json) {
						try {
							_response.data = JSON.parse(body);
							resolve(_response.data);
						} catch (e) {
							resolve(_response.data);
							logger('ERROR', options.url, 'Could not parse json', e);
						}
					} else {
						resolve(_response.data);
					}

				});
			});
		});
	};

	var api = {
		get: function(id) {
			logger('get', id);
			return new Promise(function(resolve, reject) {
				sendRequest({
					method: 'GET',
					url: `${id}`
				}).then(resolve, reject);
			});
		},
		remove: function(id, rev) {
			logger('remove', id);
			return new Promise(function(resolve, reject) {
				sendRequest({
					url: `${id}?rev=${rev}`,
					method: 'DELETE'
				}).then(resolve, reject);
			});
		},
		put: function(doc) {
			logger('put', doc);
			return new Promise(function(resolve, reject) {
				if (!doc._rev) {
					api.get(doc._id).then(function(resp) {
						doc._rev = resp._rev;
						sendRequest({
							url: `${doc._id}`,
							method: 'PUT',
							json: true,
							body: doc
						}).then(resolve, reject);
					}).catch(function(err) {
						sendRequest({
							url: `${doc._id}`,
							method: 'PUT',
							json: true,
							body: doc
						}).then(resolve, reject);
					});
				}
			});
		},
		saveAll: function(docs) {
			return new Promise(function(resolve, reject) {
				sendRequest({
					url: `_bulk_docs`,
					method: 'PUT',
					json: true,
					body: {
						docs: docs
					}
				}).then(resolve, reject);
			});
		},
		post: function(doc) {
			doc._id = require('node-uuid').v4();
			return new Promise(function(resolve, reject) {
				sendRequest({
					url: `${doc._id}`,
					method: 'PUT',
					json: true,
					body: doc

				}).then(resolve, reject);
			});
		},
		find: function(params) {
			return new Promise(function(resolve, reject) {
				sendRequest({
					method: 'GET',
					url: '_all_docs?include_docs=true',
					json: false
				}).then(function(resp) {

					var docs = [];
					resp.rows.forEach(function(row) {
						docs.push(row.doc);
					});
					var filtered = _.findLast(docs, params);
					resolve(filtered);

				}, reject);
			});
		},
		allDocs: function(params) {
			return new Promise(function(resolve, reject) {
				sendRequest({
					url: '_all_docs?include_docs=true',
					method: 'GET',
					json: false
				}).then(function(resp) {
					var docs = resp.rows.filter(function(row) {
						return row.doc;
					});

					logger('allDocs', docs);

					resolve(_.filter(docs, params));
				}, reject);
			});
		}
	};

	return api;
};

'use strict';
const _ = require('lodash');
const redis = require('redis');
const utils = require('../utils');

module.exports = function(options) {
	const logger = utils.getLogger('redis');
	logger('instance', options);
	options = options || {};

	let client;

	if (options.client) {
		client = options.client;
	} else {
		client = redis.createClient(options);
		client.on('error', function(err) {
			logger('Redis Client error', err);
		});
	}

	return {
		get: function(id) {
			logger('get', id);
			return new Promise(function(resolve, reject) {
				client.get(id, function(err, res) {
					if (err) {
						reject(err);
					}
					logger('success', res);
					resolve(JSON.parse(res));
				});
			});
		},
		put: function(doc) {
			logger('put', doc._id);
			return new Promise(function(resolve, reject) {
				client.set(doc._id, JSON.stringify(doc), function(err, res) {
					if (err) {
						reject(err);
					}
					logger('success', res);
					resolve(res);
				});
			});
		},
		remove: function(id) {
			logger('remove', id);
			return new Promise(function(resolve, reject) {
				client.del(id, function(err, res) {
					if (err) {
						reject(err);
					}
					logger('success', res);
					resolve(res);
				});
			});
		},
		allDocs: function(params) {
			return new Promise(function(resolve, reject) {
				client.keys('*', function(err, res) {
					if (err) {
						reject(err);
					}
					_(res).forEach(function(i, n) {
						logger(i, n);
					});
					logger('success', res);
					resolve(res);
				});
			});
		}
	}
};

