'use strict';
const path = require('path');
const fs = require('fs-extra');
module.exports = function(program) {
	const jpsPassbook = program.require('jps-passbook')(program);
	const SignPass = program.require('signpass');
	const logger = program.getLogger('AdminController');
	let AdminController = {};

	/**
	 * I handle saving a upload to the file system and data store.
	 * @param file
	 * @returns {*}
	 */
	function saveUpload(file) {
		return new Promise(function(resolve, reject) {
			let toFilename = path.resolve(program.config.defaults.dataPath, './uploads/' + file.originalFilename);
			let _doc = {
				_id: 'file-' + file.name,
				originalFilename: file.originalFilename,
				path: toFilename,
				size: file.size,
				name: file.name,
				type: file.type
			};
			logger('saveUpload', _doc);
			fs.copy(file.path, _doc.path, function(err) {
				if (err) {
					reject(err);
				}
				program.db.put(_doc).then(resolve, reject);
			});
		});
	}
	AdminController.saveUpload = saveUpload;

	/**
	 * I handle getting a pass type identifier
	 * @param req
	 * @param res
	 */
	AdminController.get_passTypeIdentifier = function(req, res) {
		jpsPassbook.getPassTypeIdentifier(req.params.id).then(function(resp) {
			console.log('found identifier', resp);
			if(resp){
				res.status(200).json(resp);
			} else {
				res.status(404).json(err);
			}
		}).catch(function(err) {
			res.status(404).json(err);
		});
	};


	/**
	 * I handle saving a passTypeIdentifier
	 * @param req
	 * @param res
	 */
	AdminController.post_passTypeIdentifier = function(req, res) {
		logger('post_passTypeIdentifier', req.body, req.files);
		let out = {};
		let _id = req.params.id;
		let passTypeIdentifier = req.body;
		passTypeIdentifier.passTypeIdentifier = _id;

		if (req.files && req.files.file) {
			logger('Upload file', req.files);

			saveUpload(req.files.file).then(function(_file) {
				logger('Save file', _file);

				passTypeIdentifier.p12 = _file.path;

				jpsPassbook.savePassTypeIdentifier(passTypeIdentifier, function(err, resp) {
					if (err) {
						logger('savePassTypeIdentifier - error', err);
						res.status(400).json(err);
					} else {
						res.status(201).json(resp);
					}
				});

			}).catch(function(err) {
				logger('saveUpload - error', err);
				res.status(400).json(err);
			});

		} else {
			res.status(400).json({
				error: 'Please upload a .p12 file!'
			});
		}
	};

	/**
	 * I handle downloading a pass
	 * @param req
	 * @param res
	 */
	AdminController.get_downloadPass = function(req, res) {
		let id = req.params.id;
		logger('get_downloadPass', id);
		if (id) {
			program.db.get(id).then(function(resp) {
				logger('found pass', resp._id);
				logger('found pkpassFilename', resp.pkpassFilename);
				res.set('Content-Type', 'application/vnd.apple.pkpass')
					.status(200)
					.download(resp.pkpassFilename);
			}).catch(function(err) {
				res.status(404).json(err);
			});
		} else {
			res.status(400).json('Must provide id!');
		}
	};

	/**
	 * I handle getting a pass
	 * @param req
	 * @param res
	 */
	AdminController.get_signPass = function(req, res) {
		let id = req.params.id
		logger('get_signPass', id);
		program.db.get(id).then(function(resp) {
			jpsPassbook.createPassPromise(resp).then(function(p) {
				jpsPassbook.signPassPromise(p).then(function(_resp) {
					res.status(200).json(_resp);
				}).catch(function(err) {
					res.status(404).json(err);
				});
			}).catch(function(err) {
				logger('get_signPass - error', err);
				res.status(400).json(err);
			});
		}).catch(function(err) {
			res.status(404).json(err);
		});
	};

	/**
	 * I handle finding data
	 * @param req
	 * @param res
	 */
	AdminController.get_find = function(req, res) {
		logger('get_find', req.query);
		program.db.find(req.query).then(function(resp) {
			logger('get_find', 'resp', resp);
			if(resp){
				res.status(200).json(resp);
			} else {
				res.status(404).json(err);
			}

		}).catch(function(err) {
			res.status(404).json(err);
		});
	};



	return AdminController;


};

'use strict';

const multipart = require('connect-multiparty');
const bodyParser = require('body-parser');
const path = require('path');
const fs = require('fs-extra');
const assert = require('assert');
const _ = require('lodash');


module.exports = function(program) {
	const config = program.config.defaults;
	const logger = program.getLogger('AppController');
  let AppController = {};

	AppController.index = function(req, res, next){
		res.status(200).json({
			message: config.name
		});
	};

	/**
	 * I handle displaying an upload form.
	 * @param req
	 * @param res
	 * @param next
     */
	AppController.get_upload = function(req, res, next){
		res.writeHead(200, {
			'content-type': 'text/html'
		});
		res.end(
			'<form action="" enctype="multipart/form-data" method="post">' +
			'<input type="file" name="files" multiple="multiple"><br>' +
				//		'<input type="file" name="file"><br>' +
			'<input type="submit" value="Upload">' +
			'</form>'
		);
	};

	/**
	 * I handle processing a file upload.
	 * @param req
	 * @param res
	 * @param next
     */
	AppController.post_upload = function(req, res, next){
		var out = [];
		var files = [];
		var file = null;
		var toFilename;

			logger('upload', req.files);

			// parse a file upload
			files = req.files;
			for (var f in files) {
				file = files[f];
				logger('upload', 'file', file);
				toFilename = path.resolve(config.dataPath, './passes/' + req.body._id + '.raw/' + file.originalFilename);

				try {
					//	fs.writeFileSync(toFilename, fs.readFileSync(file.path));
					fs.copySync(file.path, toFilename);
					out.push(toFilename);
					logger('upload', 'to', toFilename);
					fs.removeSync(file.path);

				} catch (err) {
					console.error('Oh no, there was an error: ' + err.message);
					res.status(400).json({
						error: err.message
					});
				}


			}
			res.status(200).json(out);


	};

	AppController.post_log = function(req, res, next){
		var dataLog = {};
		dataLog._id = _.uniqueId('log-');
		dataLog.docType = 'log';
		dataLog.data = req.body;
		dataLog.created_at = _.now();

		_.defer(function() {
			program.db.put(dataLog).then(function(resp) {
				res.status(200).json(resp);
			}).catch(function(err) {
				res.status(400).json(err);
			});
		}, 250);
	};

  return AppController;

};

'use strict';
module.exports = function (program) {
	const path = require('path');
	const url = require('url');
	const express = require('express');
	const expressValidator = require('express-validator');
	const methodOverride = require('method-override');
	const bodyParser = require('body-parser');
	const session = require('express-session');
	const RedisStore = require('connect-redis')(session);
	const Router = express.Router;
	const jsonParser = bodyParser.json();
	const passport = require('passport');
	const OAuth2Strategy = require('passport-oauth').OAuth2Strategy;
	const GitHubStrategy = require('passport-github2').Strategy;
	const LocalStrategy = require('passport-local').Strategy;
	const User = require(path.resolve(__dirname, '../models/user.js'));
	const Users = require(path.resolve(__dirname, '../models/users.js'))(program.db);
	const config = program.config.defaults;
	const db = program.db;
	const logger = program.getLogger('AuthController');
	var GITHUB_CLIENT_ID = config.passport.development.github.clientID;
	var GITHUB_CLIENT_SECRET = config.passport.development.github.clientSecret;
	var OAUTH_CALLBACK_URL = config.passport.development.github.callbackURL;

	if (process.env.NODE_ENV === 'production') {
		GITHUB_CLIENT_ID = config.passport.production.github.clientID;
		GITHUB_CLIENT_SECRET = config.passport.production.github.clientSecret;
		OAUTH_CALLBACK_URL = config.passport.production.github.callbackURL;
	}

	const OAUTH_CLIENT_SECRET = GITHUB_CLIENT_SECRET;
	const OAUTH_CLIENT_ID = GITHUB_CLIENT_ID;
	const OAUTH_AUTH_URL = 'https://github.com/login/oauth/authorize';
	const OAUTH_TOKEN_URL = 'https://github.com/login/oauth/access_token';


	logger('OAUTH_CLIENT_SECRET', OAUTH_CLIENT_SECRET);
	logger('OAUTH_CLIENT_ID', OAUTH_CLIENT_ID);
	logger('OAUTH_AUTH_URL', OAUTH_AUTH_URL);
	logger('OAUTH_TOKEN_URL', OAUTH_TOKEN_URL);
	logger('OAUTH_CALLBACK_URL', OAUTH_CALLBACK_URL);


	// =========================================================================
	// LOCAL SIGNUP ============================================================
	// =========================================================================
	passport.use('local-signup', new LocalStrategy({
			usernameField: 'email',
			passwordField: 'password',
			passReqToCallback: true
		},
		function (req, email, password, done) {
			logger('local-signup', email);

			process.nextTick(function () {
				Users.find({
					'email': email
				}, function (err, user) {
					if (err) {
						logger('local-signup', 'error', 'Fatal error');
						return done(err);
					}

					if (user) {
						logger('local-signup', 'error', 'found existing user', user);
						return done(null, false, req.flash('signupMessage', 'That email is already taken.'));
					} else {
						logger('local-signup', 'registering', email);

						var newUser = new User({
							//username: email,
							email: email
						});
						newUser.password = newUser.generateHash(password);

						Users.save(newUser, function (err, u) {
							if (err) {
								logger('saveUser', 'error', err);
								throw err;
							}
							logger('local-signup', 'success', u);
							return done(null, u);
						});
					}
				});
			});
		}));
	// =========================================================================
	// LOCAL LOGIN =============================================================
	// =========================================================================
	passport.use('local-login', new LocalStrategy({
		usernameField: 'email',
		passwordField: 'password',
		passReqToCallback: true
	}, function (req, email, password, done) {
		logger('local-login', email);

		Users.findByEmail(email, function (err, user) {
			user = new User(user);
			logger('local-login', 'findOne', user);

			if (err) {
				logger('local-login', 'error', err);
				return done(err);
			}

			if (!user) {
				logger('local-login', 'No User Found!');
				return done(null, false, req.flash('loginMessage', 'No user found.'));
			}

			if (!user.checkPassword(password, user.password)) {
				logger('local-login', 'invalidePassword', user.password, password);
				return done(null, false, req.flash('loginMessage', 'Oops! Wrong password.'));
			}

			return done(null, user);
		});
	}));


	passport.use('oauth2', new OAuth2Strategy({
			authorizationURL: OAUTH_AUTH_URL,
			tokenURL: OAUTH_TOKEN_URL,
			clientID: OAUTH_CLIENT_ID,
			clientSecret: OAUTH_CLIENT_SECRET,
			callbackURL: OAUTH_CALLBACK_URL
		},
		function (accessToken, refreshToken, profile, done) {
			logger('accessToken', accessToken, refreshToken, profile);
			Users.findOrCreate(profile, done);
		}
	));

	passport.use('github', new GitHubStrategy({
			clientID: OAUTH_CLIENT_ID,
			clientSecret: OAUTH_CLIENT_SECRET,
			callbackURL: OAUTH_CALLBACK_URL
		},
		function (accessToken, refreshToken, profile, done) {
			process.nextTick(function () {
				logger('accessToken', accessToken);
				logger('refreshToken', refreshToken);
				logger('profile', profile);

				return Users.findOrCreate(profile, done);
			});
		}
	));

	passport.serializeUser(function (user, done) {
		logger('serializeUser', user);
		Users.findOrCreate(user, done);
	});

	passport.deserializeUser(function (id, done) {
		logger('deserializeUser', id);
		Users.find(id, done);
	});

	function restrict(req, res, next) {
		if (req.user) {
			return next();
		} else {
			res.send(403); // Forbidden
		}
	}

	function ensureAuthenticated(req, res, next) {
		logger('ensureAuthenticated', req.url);
		if (req.isAuthenticated()) {
			return next();
		}
		res.redirect('/login')
	}

	// TODO: do any checks you want to in here
	function isAuthenticated(req, res, next) {
		logger('isAuthenticated', req.session);
		if (req.session && req.session.authenticated) {
			return next();
		}
		res.redirect('/login');
	}

	let AuthController = {
		ensureAuthenticated: ensureAuthenticated,
		isAuthenticated: isAuthenticated,
		restrict: restrict,
		get_index: function (req, res, next) {
			res.render('index', {
				user: req.user
			});
		},
		get_login: function (req, res, next) {
			if (req.session) {
				logger('get_login', 'session', req.session);
			}
			if (req.user) {
				logger('get_login', 'user', req.user);
			}
			res.render('login', {
				user: req.user,
				message: req.flash('loginMessage', 'Please sign in')
			});

		},
		post_login: function (req, res, next) {
			logger('post_login', req.body);

			//  localAuth(req, res, next);
			if (req.user) {
				res.redirect('/account');
			}

			Users.validate(_user, function (err, resp) {
				if(err){
					res.format({
						html: function () {
							res.render('login', {
								user: req.user,
								message: req.flash('loginMessage', err.message)
							});
						},
						json: function () {
							res.status(200).json(resp);
						}
					});
				}
				res.format({
					html: function () {
						res.redirect('/account');
					},
					json: function () {
						res.status(200).json(resp);
					}
				});
			});
		},
		get_logout: function (req, res, next) {
			logger('get_logout');
			req.logout();
			res.redirect('/login', {
				message: req.flash('loginMessage', 'You have been logged out.')
			});
		},
		get_me: function (req, res, next) {
			logger('get_me', req.url, req.session);
			res.status(200).json(req.user);
		},
		post_me: function (req, res, next) {
			logger('post_me', req.url, req.user);
			res.status(200).json(req.user);
		},
		get_provider: function (req, res, next) {
			logger('get_provider');
			passport.authenticate('oauth2');
		},
		get_provider_callback: function (req, res, next) {
			logger('get_provider_callback', req.url);
			res.redirect('/account');
		},
		get_register: function (req, res, next) {
			logger('get_register');
			res.render('register', {
				message: req.flash('signupMessage')
			});
		},
		post_register: function (req, res, next) {
			logger('post_register', req.body);

			//user.password = user.generateHash(user.password);
			//  req.body.password = program.utils.checksum(req.body.password, 'sha1');
			logger('post_register', req.body);
			Users.findOrCreate(req.body, function (err, u) {
				if (err) {
					res.format({
						html: function () {
							res.render('register', {
								message: err
							});
						},
						json: function () {
							res.status(400).json(err);
						}
					});
				} else {
					res.format({
