'use strict';
module.exports = function(program) {
  const _ = require('lodash');
  let logger = program.getLogger('DbController');
  var Controller = {};
  Controller = {
    get_doc: function(req, res, next) {
      if (req.params.id) {
        program.db.get(req.params.id, req.params).then(function(resp) {
          res.status(200).json(resp);
        }).catch(function(err) {
          res.status(404).json(err);
        });
      } else {
        Controller.get_docs(req, res, next);
      }
    },
    get_docs: function(req, res, next) {
      program.db.allDocs(req.query).then(function(resp) {
        res.status(200).json(resp);
      }).catch(function(err) {
        res.status(400).json(err);
      });
    },
    put_doc: function(req, res, next) {
      _.defer(function() {
        program.db.put(req.body, req.params.id, req.query.rev).then(function(resp) {
          res.status(200).json(resp);
        }).catch(function(err) {
          res.status(404).json(err);
        });
      });
    },
    post_doc: function(req, res, next) {
      program.db.post(req.body).then(function(resp) {
        res.status(201).json(resp);
      }).catch(function(err) {
        res.status(400).json(err);
      });
    },
    delete_doc: function(req, res, next) {
      _.defer(function() {
        program.db.remove(req.params.id).then(function(resp) {
          res.sendStatus(200);
        }).catch(function(err) {
          res.sendStatus(404);
        });
      });
    }
  };

  return Controller;
  logger('initialized');
};

'use strict';
const _ = require('lodash');
const assert = require('assert');
const Registration = require('../models/registration');
const Device = require('../models/device');
const Passes = require('../models/passes');

module.exports = function(program) {
	var logger = program.getLogger('controller:devices');
	return {
		/*
		 # Registration
		 # register a device to receive push notifications for a pass
		 #
		 # POST /v1/devices/<deviceID>/registrations/<typeID>/<serial#>
		 # Header: Authorization: ApplePass <authenticationToken>
		 # JSON payload: { "pushToken" : <push token, which the server needs to send push notifications to this device> }
		 #
		 # Params definition
		 # :device_id      - the device's identifier
		 # :pass_type_id   - the bundle identifier for a class of passes, sometimes refered to as the pass topic, e.g. pass.com.apple.backtoschoolgift, registered with WWDR
		 # :serial_number  - the pass' serial number
		 # :pushToken      - the value needed for Apple Push Notification service
		 #
		 # server action: if the authentication token is correct, associate the given push token and device identifier with this pass
		 # server response:
		 # --> if registration succeeded: 201
		 # --> if this serial number was already registered for this device: 304
		 # --> if not authorized: 401

		 post '/v1/devices/:device_id/registrations/:pass_type_id/:serial_number'
		 */
		post_device_registration: function(req, res, next) {

			logger('post_device_registration', req.url);
			logger('post_device_registration', 'params', req.params);
			logger('post_device_registration', 'Handling registration request...');

			let device_id = req.params.device_id;
			let pass_type_id = req.params.pass_type_id;
			let serial_number = req.params.serial_number;
			let push_token = req.body.pushToken;
			let authentication_token = req.get('Authorization');
			let device = null;
			let registration = null;

			logger('post_device_registration', 'authentication =', authentication_token);
			logger('post_device_registration', 'device_id =', device_id);
			logger('post_device_registration', 'pass_type_id =', pass_type_id);
			logger('post_device_registration', 'serial_number =', serial_number);
			logger('post_device_registration', 'push_token =', push_token);

			if (!authentication_token) {
				res.status(401).json({
					error: 'Unauthorized'
				});
			} else {

				try {
					device = new Device({
						//_id: 'device-' + device_id,
						deviceLibraryIdentifier: device_id,
						passTypeIdentifier: pass_type_id,
						authorization: authentication_token,
						pushToken: push_token,
						serialNumber: serial_number
					});

					registration = new Registration({
						//pass_id: serial_number,
						serial_number: serial_number,
						pass_type_id: pass_type_id,
						device_id: device._id,
						auth_token: authentication_token,
						deviceLibraryIdentifier: device_id,
						push_token: push_token
					});

					//# The device has already registered for updates on this pass
					program.db.get(registration._id).then(function(reg) {
						logger('post_device_registration', 'found', registration._id);
						logger('post_device_registration', 'returning 200');
						res.status(200).json(reg);
					}).catch(function(err) {
						program.db.saveAll([device, registration]).then(function(resp) {
							logger('post_device_registration', 'inserted', resp);
							logger('post_device_registration', 'returning 201');
							res.status(201).json(resp);
						}).catch(function(err) {
							logger('post_device_registration', 'error', err);
							res.status(400).json(err);
						});
					});


				} catch (e) {
					logger('post_device_registration', 'error', e);
					//	program.db.saveSync(registration);
					//	program.db.saveSync(device);
					res.status(400).json(e);
				}

			}
		},
		/**
		 * # Unregister
		 #
		 # unregister a device to receive push notifications for a pass
		 #
		 # DELETE /v1/devices/<deviceID>/registrations/<passTypeID>/<serial#>
		 # Header: Authorization: ApplePass <authenticationToken>
		 #
		 # server action: if the authentication token is correct, disassociate the device from this pass
		 # server response:
		 # --> if disassociation succeeded: 200
		 # --> if not authorized: 401
		 * @param req
		 * @param res
		 * @param next
		 */
		delete_device_registration: function(req, res, next) {
			logger('delete_device_registration', req.url);

			let authentication_token = `${req.get('Authorization')}`;
			let device_id = req.params.device_id;
			let pass_type_id = req.params.pass_type_id;
			let serial_number = req.params.serial_number;

			assert.ok(device_id, 'has device id');
			assert.ok(pass_type_id, 'has pass type id');
			assert.ok(serial_number, 'has serial number');

			let uuid = device_id + '-' + serial_number;
			let registration;

			let device = new Device({
				deviceLibraryIdentifier: device_id
			});

			registration = new Registration({
				serial_number: serial_number,
				auth_token: authentication_token,
				device_id: device._id
			});

			var tokensMatch = false;

			logger('delete_device_registration', 'req.authenticationToken =', authentication_token);
			if (!authentication_token) {
				res.status(401).json({
					error: 'Unauthorized'
				});
			} else {

				logger('delete_device_registration', 'Finding registration', registration._id);

				program.db.get(registration._id).then(function(reg) {
					tokensMatch = (authentication_token === reg.auth_token);

					logger('delete_device_registration', 'found', reg._id);
					logger('delete_device_registration', 'checking tokens', tokensMatch);

					if (tokensMatch) {

						logger('delete_device_registration', 'req.authenticationToken =', authentication_token);
						logger('delete_device_registration', 'registration token =', reg.auth_token);
						logger('delete_device_registration', 'Pass and authentication token match.');


						program.db.remove(reg._id).then(function(resp) {
							res.status(200).json(resp);

						}).catch(function(err) {
							res.status(404).json({
								error: 'Registration does not exist'
							});
						});

					} else {
						logger('delete_device_registration', 'Pass and authentication token DO NOT match.');
						res.status(401).json({
							error: 'Pass and authentication do not token match.'
						});
					}

				}).catch(function(err) {
					res.status(404).json({
						error: 'Registration does not exist'
					});
				});
			}
		},
		/**
		 *

		 # Updatable passes
		 #
		 # get all serial #s associated with a device for passes that need an update
		 # Optionally with a query limiter to scope the last update since
		 #
		 # GET /v1/devices/<deviceID>/registrations/<typeID>
		 # GET /v1/devices/<deviceID>/registrations/<typeID>?passesUpdatedSince=<tag>
		 #
		 # server action: figure out which passes associated with this device have been modified since the supplied tag (if no tag provided, all associated serial #s)
		 # server response:
		 # --> if there are matching passes: 200, with JSON payload: { "lastUpdated" : <new tag>, "serialNumbers" : [ <array of serial #s> ] }
		 # --> if there are no matching passes: 204
		 # --> if unknown device identifier: 404
		 #
		 #
		 * @param req
		 * @param res
		 * @param next
		 */
		get_device_passes: function(req, res, next) {
			let authentication_token = req.get('Authorization');
			let device_id = req.params.device_id;
			let pass_type_id = req.params.pass_type_id;
			let serials = [];
			//	let serial_number = req.params.serial_number;

			assert(device_id, 'has device id');
			assert(pass_type_id, 'has pass type id');

			logger('get_device_passes', authentication_token);
			logger('get_device_passes', req.method, req.url);

			if (!authentication_token) {
				res.status(401).json({
					error: 'Unauthorized'
				});

			} else {

				logger('get_device_passes', 'Make sure device is registered');
				program.db.find({
					passTypeIdentifier: pass_type_id,
					authorization: authentication_token,
					deviceLibraryIdentifier: device_id
				}).then(function(devices) {

					if (devices) {

						serials = _.pluck(devices, 'serialNumber');

						logger('get_device_passes', 'get passes by pass_type_id');
						logger('get_device_passes', 'Return matching passes');

						res.status(200).json({
							lastUpdated: Date.now().toString(),
							serialNumbers: serials
						});
					} else {
						res.status(404).json({
							error: 'Device not registered'
						});
					}
				}).catch(function(err) {
					res.status(404).json(err);
				});


			}
		}
	};
};

'use strict';
const assert = require('assert');
const _ = require('lodash');
/*
 * Methods
 * to find the passes that a given device has registered for,
 * to find the devices that have registered for a given pass.
 *
 *  Registration is a many-to-many relationship: a single device can register for updates to multiple passes,
 *  and a single pass can be registered by multiple devices.*/
module.exports = function(program) {
	var db = program.db;
	var logger = program.getLogger('controller:passes');
	var Passbook = new program.modules.Passbook(program);
	var Passes = program.models.Passes;
	var Pass = program.models.Pass;

	/*
	 * To handle the device registration, do the following:

	 Verify that the authentication token is correct. If it doesnt match, immediately return the HTTP status 401 Unauthorized and disregard the request.
	 Store the mapping between the device library identifier and the push token in the devices table.
	 Store the mapping between the pass (by pass type identifier and serial number) and the device library identifier in the registrations table.
	 */
	return {

		get_passes: function(req, res) {
			var self = this;
			var auth = req.get('Authorization');
			var pass_type_id = req.params.pass_type_id;
			var device_id = req.params.device_id;
			var serial_number = req.params.serial_number;
			var lastUpdated = req.query.updatedSince;

			//assert.ok(pass_type_id, 'has pass type id');

			logger('Handling pass delivery request...');
			logger('Authorization=', auth);
			logger('pass_type_id=', pass_type_id);
			logger('serial_number=', serial_number);
			logger('device_id=', device_id);

			if (!auth) {
				logger('get_passes:unauthorized');
				return res.status(401).json({
					error: 'Unauthorized'
				});

			} else {
				Passes.find({
					passTypeIdentifier: pass_type_id,
					serialNumber: serial_number
				}).then(function(resp) {
					logger('get_passes:success');

					if (lastUpdated > resp.lastUpdated) {
						res.status(204).json({});
					} else {
						res.status(200).json(resp);
					}

				}).catch(function(err) {
					logger('get_passes:error', err);
					res.status(404).json(err);
				});
			}
		},


		post_pass: function(req, res) {
			var p = new Pass(req.body);
			logger('post_pass', p._id);

			Passes.save(p).then(function(resp) {
				res.status(201).json(resp);
				/*	Passbook.createPass(resp, function(err, obj) {
						res.status(201).json(resp);
					});*/
			}).catch(function(err) {
				res.status(404).json(err);
			});

		},


		put_pass: function(req, res) {
			var p = new Pass(req.body);
			var id = req.params.id
			p._id = id;
			logger('put_pass', id);
			Passes.save(p).then(function(resp) {
				logger('save', resp);
				res.status(200).json(resp);
			}).catch(function(err) {
				res.status(404).json(err);
			});
		},
		get_all_passes: function(req, res) {
			Passes.getPasses(req.params).then(function(resp) {
				res.status(200).json(resp);
			}).catch(function(err) {
				res.status(404).json(err);
			});
		},
		get_pass: function(req, res) {
			var id = req.params.id
			logger('get_pass', id);
			db.get(id).then(function(resp) {
				res.status(200).json(resp);
			}).catch(function(err) {
				res.status(204).json(err);
			});
		},
		sign_pass: function(req, res) {
			var id = req.params.id
			logger('sign_pass', id);
			Passes.get(id).then(function(resp) {
				program.modules.Passbook.createPass(mockPass, function(err, p) {
					//	res.status(200).json(p);
					res.set('Content-Type', 'application/vnd.apple.pkpass')
						.status(200)
						.download(p.pkpassFilename);
				});
				//	res.status(200).json(resp);
			}).catch(function(err) {
				res.status(404).json(err);
			});
		},
		delete_pass: function(req, res) {
			var id = req.params.id;
			logger('delete_pass', id);

			Passes.remove(id).then(function(resp) {
				res.status(200).json(resp);
			}).catch(function(err) {
				res.status(404).json(err);
			});
		},
		get_find_pass: function(req, res) {
			var params = req.query;
			logger('get_find_pass', params);
			Passes.find(params).then(function(resp) {
				res.status(200).json(resp);
			}).catch(function(err) {
				res.status(404).json(err);
			});
		}
	};
};

'use strict';

var logger = require('debug')('jps-passbook-manager:store');
var instance = null;
var _ = require('lodash');
var Store = require('jfs');

function FileDataStore(name, options) {
	//logger('new FileDataStore', name, options)
	var db = new Store(name, options || {
			saveId: '_key',
			//	type: 'single',
			pretty: true
		});
	var _ds = {
		getUUID: function (prefix) {
			return prefix || 'doc-' + require('node-uuid').v4();
		},
		findBy: function (params) {
			logger('findBy', params);
			return this.find(params).then(function (resp) {
				return _(resp).first();
			});
		},

		find: function (params) {
			let self = this;
			logger('find', params);
			return new Promise(function (resolve, reject) {
				let _out, _docs = [];

				self.allDocs(params).then(function (resp) {
					_out = _.where(resp.rows, params);

					if (_out && _out.length > 0) {
						logger('find.success', _out.length);
						resolve(_out);
					} else {
						/*TODO - Never reject, just return empty */
						reject({
							error: 'No match found',
							params: params
						});
					}

				});
			});
		},
		allDocs: function (params) {
			return new Promise(function (resolve, reject) {
				let _docs = [], _obj;
				logger('allDocs', params);
				_.defer(function () {
					db.all(function (err, objs) {
						if (err) {
							reject(err);
						}
						for (_obj in objs) {
							_docs.push(objs[_obj]);
						}
						if (params) {
							_docs = _.filter(_docs, params);
						}
						resolve({
							rows: _docs
						});
					});
				});
			});
		},
		put: function (doc, id) {
			logger('put', doc._id);
			return new Promise(function (resolve, reject) {
				_.defer(function () {
					db.save(doc._id || id, doc, function (err) {
						if (err) {
							logger('put.error', err);
							reject(err);
						} else {
							logger('put.success', doc._id);
							resolve(doc);
						}

					});
				});
			});
		},
		post: function (doc) {
			doc._id = this.getUUID();
			logger('post', doc);
			return new Promise(function (resolve, reject) {
				_.defer(function () {
					db.save(doc, function (err) {
						if (err) {
							logger('post.error', err);
							reject(err);
						} else {
							logger('post.success', doc._id);
							resolve(doc);
						}
					});
				});
			});
		},
		remove: function (id) {
			logger('remove', id);
			return new Promise(function (resolve, reject) {
				_.defer(function () {
					db.delete(id, function (err) {
						if (err) {
							logger('remove.error', err);
							reject(err);
						} else {
							logger('remove.success', id);
							resolve(id);
						}
					});
				});
			});
		},
		get: function (id) {
			logger('get', id);
			return new Promise(function (resolve, reject) {
				_.defer(function () {
					db.get(id, function (err, obj) {
						if (err) {
							logger('get.error', err);
							reject(err);
						} else {
							logger('get.success', id);
							resolve(obj);
						}
					});
				});
			});
		},
		saveAll: function (docs) {
			let self = this;
			return new Promise(function (resolve, reject) {
				let saves = [];

				let _done = _.after(docs.length, function () {
					logger('saveAll.success');
					resolve(saves);
				});
				logger('saveAll', docs.length);

				_.forEach(docs, function (doc) {
					doc._id = doc._id || self.getUUID();
					self.put(doc).then(function (resp) {
						logger('put.success', resp._id);
						saves.push(resp);
						_done();
					}).catch(function (err) {
						_done(err);
					});
				});
			});
		}
	};
	_ds.saveSync = db.saveSync;
	_ds.allSync = db.allSync;
	_ds.getSync = db.getSync;

	_ds.bulkDocs = _ds.saveAll;
	_ds.findOne = _ds.findBy;

	instance = _ds;

	return instance;
}

module.exports.getInstance = function () {
	if (instance) {
		return instance;
	} else {
		return new FileDataStore();
	}
};

exports.FileDataStore = FileDataStore;

'use strict';

const fs = require('fs-extra');
const async = require('async');
const glob = require('glob');
const path = require('path');
const Pass = require('./models/pass');
const assert = require('assert');
const _ = require('lodash');
const child_process = require('child_process');
const debug = require('debug');

/**
 * jpsPassbook contains methods for creating/validating/signing passes.
 * @param program
 * @returns {{
 * savePassTypeIdentifier: savePassTypeIdentifier,
 * savePassTypeIdentifierPromise: savePassTypeIdentifierPromise,
 * getPassTypeIdentifier: getPassTypeIdentifier,
 * getPassCerts: getPassCerts,
 * signPass: signPass,
 * validatePass: validatePass,
 * createPass: createPass,
 * batchPromise: batchPromise,
 * createPassPromise: createPassPromise,
 * signPassPromise: signPassPromise,
 * validatePassPromise: validatePassPromise
 * }}
 */
module.exports = function jpsPassbook(program) {
	var utils = program.utils;
	let logger = program.getLogger('passbook');
	let db = program.db;
	let config = program.config.defaults;
	const SignPass = program.require('signpass');

	/**
	 * I handle getting the certificates for a pass.
	 * @param id
	 * @param cb
	 * @returns {*}
	 */
	function getPassCerts(id, cb) {
		return new Promise(function (resolve, reject) {
			db.findOne({
				docType: 'pass-type-id',
				passTypeIdentifier: id
			}).then(function (resp) {
				cb(null, resp);
			}).catch(function (err) {
				cb(err);
			});
		});
	}

	/**
	 * I handle saving a pass type id to database and create pems.
	 * @param obj
	 * @param cb
	 * @returns {*}
	 */
	function savePassTypeIdentifier(obj, cb) {
		let outputPath = path.resolve(program.config.defaults.dataPath, './', '');
		fs.ensureDirSync(outputPath);
		obj.wwdr = obj.wwdr || path.resolve(__dirname, '../certificate/wwdr-authority.pem');

		logger('savePassTypeIdentifier', 'outputPath', outputPath);
		logger('savePassTypeIdentifier', 'passed', obj);

		if (!obj.p12) {
			return cb({
				error: 'Must provide path to .p12 certificate'
			});
		} else if (!obj.passphrase) {
			return cb({
				error: 'Must provide passphrase to .p12 certificate'
			});
		}

		SignPass.createPems({
			output: outputPath,
			wwdr: obj.wwdr,
			passTypeIdentifier: obj.passTypeIdentifier,
			passphrase: obj.passphrase,
			p12: obj.p12
		}, function (err, resp) {
			if (err) {
				cb(err, null);
			}
			logger('createPems', 'success', resp._id);
			program.db.put(resp).then(function (o) {
				if (cb) {
					cb(null, o);
				}

			}).catch(function (err) {
				if (cb) {
					cb(err);
				}
			});
		});
	}

	/**
	 * I handle saving the pass type identifier.
	 * @param obj
	 * @returns {*}
	 */
	function savePassTypeIdentifierPromise(obj) {
		return new Promise(function (resolve, reject) {
			savePassTypeIdentifier(obj, function (err, resp) {
				if (err) {
					reject(err);
				}
				resolve(resp);
			});
		});
	}

	/**
	 * I handle saving a upload to database and move to data directory
	 * @param file
	 * @returns {*}
	 */
	function saveUpload(file) {
		return new Promise(function (resolve, reject) {
			var toFilename = path.resolve(program.config.defaults.dataPath, './uploads/' + file.originalFilename);
			var _doc = {
				_id: 'file-' + file.name,
				originalFilename: file.originalFilename,
				path: toFilename,
				size: file.size,
				name: file.name,
				type: file.type
			};
			logger('saveUpload', _doc);
			fs.copy(file.path, _doc.path, function (err) {
				if (err) {
					reject(err);
				}
				program.db.put(_doc).then(resolve, reject);
			});
		});
	}

	/**
	 * I handle signing a .raw pass with signpass bin.
	 * Fetch pass certs from db and use signPass\
	 * Fetch pass info from pass.json file instead of db.
	 * @param pass
	 * @param callback
	 */
	function signPass(pass, callback) {
		pass = new Pass(pass);
		let signpass = null;
		let _passFilename = pass.filename || path.resolve(
				program.config.defaults.dataPath, `./${pass.passTypeIdentifier}/passes/${pass._id}.raw`);
		let options = {
			passFilename: null,
			cert: null,
			passphrase: null,
			key: null,
			wwdr: null,
			outputFilename: null,
			compress: true
		};

		logger('signPass');
		logger('signPass', 'pass', pass._id);
		logger('signPass', 'filename', pass.filename);

		//	assert(pass.rawFilename, 'has rawFilename');

		// TODO: SignPass instance using cert info
		getPassCerts(pass.passTypeIdentifier, function (err, _certs) {
			logger('getPassCerts', _certs._id);
			if (err) {
				callback(err);
			}
			_certs.outputFilename = _passFilename.replace('.raw', '.pkpass');
			_certs.passFilename = pass.filename || pass.rawFilename || _passFilename;

			logger('signPass', 'certs', _certs.cert);
			//		logger('sign pass options', _certs);
			//	callback(null, pass);
			signpass = new SignPass(_certs);
			signpass.signPromise().then(function (resp) {
				logger('signPass', 'success', resp);
				pass.pkpassFilename = resp.dest;
				savePass(pass, function (err, p) {
					callback(err, resp);
				});
			}).catch(function (err) {
				callback(err);
			});

		});
	}

	/**
	 * Validate the contents of a signed pass
	 */
	function validatePass(pass, callback) {
		let _passFilename = pass.filename || pass.rawFilename || path.resolve(program.config.defaults.dataPath,
				`./${pass.passTypeIdentifier}/passes/${pass._id}.raw`);
		let pathToPass = _passFilename;
		let passDir = path.resolve(pathToPass, './');
		let files = fs.readdirSync(passDir);
		let manifest = {};
		let _manifest = {};
		let _dsStoreFilename = path.resolve(passDir, './.DS_Store');
		let _manifestFilename = path.resolve(passDir, './manifest.json');


		logger('validatePass', pathToPass);

		glob(passDir + '/**/*.*', function (err, files) {
			logger('validatePass', 'files', files);
			if (err) {
				callback(err)
			}

			let _done = _.after(files.length, function () {
				fs.writeFile(_manifestFilename, JSON.stringify(_manifest), function (err) {
					if (err) {
						callback(err)
					}
					logger('generate_json_manifest', 'writing', _manifestFilename);
					fs.remove(_dsStoreFilename, function () {
						callback(null, pass);
					});

				});
			});

			_.forEach(files, function (file) {
				logger('checksum', file);
				_manifest[file.replace(passDir + path.sep, '')] = utils.checksum(fs.readFileSync(file), 'sha1');
				_done();
			});
		});
	}

	/**
	 * Copy pass assets from ./templates dir to pass destination.
	 * @param type
	 * @param dest
	 * @param callback
	 */
	function copyAssets(type, dest, callback) {
		let templatesDir = path.resolve(__dirname, '../templates/' + type + '.raw/');

		logger('copyAssets', 'from', templatesDir);
		logger('copyAssets', 'to', dest);
		fs.ensureDir(dest, function (err) {
			if (err) {
				callback(err);
			}
			fs.copy(templatesDir, dest, function (err) {
				if (err) {
					callback(err);
				}
				if (callback) {
					callback(null, dest);
				}
			});
		});

	}

	/**
	 * I handle creating the pass.raw folder and writing the pass.json file into it.
	 * @param pass
	 * @param cb
	 * @returns {*}
	 */
	function createPass(pass, cb) {
		logger('createPass', '============================= start');

		let _pass = new Pass(pass);
		let passName = pass._id;

		return async.waterfall([

				//1. Build filenames
				function (callback) {
					logger('createPass', '1. Build file names');
					passName = passName.replace(/\W/g, '-');
					passName = passName.replace(' ', '-');

					let passPath = path.resolve(config.dataPath,
						pass.passTypeIdentifier,
						'./passes/',
						`./${passName}.raw`);

					logger('createPass', 'passType =', pass.type);
					logger('createPass', 'passName =', passName);
					logger('createPass', 'passPath =', passPath);
					callback(null, pass.type, passPath);
				},

				//2. Copy assets
				function (_type, _path, callback) {
					logger('createPass', '2. Copy assets');
					copyAssets(_type, _path, callback);
				},

				//3. Create pass.json
				function (_path, callback) {
					let passJsonPath = path.resolve(_path, './pass.json');
					let passPath = _path;
					logger('createPass', '3. Create pass.json');
					logger('createPass', 'writeFile', passJsonPath);
					fs.writeFile(passJsonPath, JSON.stringify(pass), function (err) {
						if (err) {
							callback(err);
						}
						pass.filename = path.resolve(passPath);
						pass.rawFilename = path.resolve(passPath);
						callback(null, pass);
					});
				},

				//4. Validate
				function (_pass, callback) {
					logger('validatePass', '4.');
					validatePass(_pass, callback);
				},

				/*
				 //4. sign
				 function(_pass, callback) {
				 logger('sign', '4. Sign Pass');
				 signPass(_pass, callback);
				 },
				 */

				//5. Save
				function (_pass, callback) {
					logger('createPass', '5. Save to database');
					savePass(_pass, callback);
				}
			],
			function (err, resp) {
				logger('createPass', '============================= complete');
				if (err) {
					cb(err);
				}
				cb(null, resp);
			});
	}

	/**
	 * Save pass to data store.
	 * @param pass
	 * @param sign
	 * @returns {*}
	 */
	function savePass(pass, callback) {
		pass = new Pass(pass);
		logger('savePass', pass._id);
		program.db.put(pass).then(function (resp) {
			logger('savePass', 'success', resp._id);
			callback(null, resp);
		}).catch(function (err) {
			logger('savePass', 'error', err);
			callback(err);
		});
	}

	return {
		savePassTypeIdentifier: savePassTypeIdentifier,
		savePassTypeIdentifierPromise: savePassTypeIdentifierPromise,
		getPassTypeIdentifier: function (id) {
			return new Promise(function (resolve, reject) {
				getPassCerts(id, function (err, resp) {
					if (err) {
						reject(err);
					} else {
						resolve(resp);
					}
				});
			});
		},
		getPassCerts: getPassCerts,
		signPass: signPass,
		validatePass: validatePass,
		createPass: createPass,
		batchPromise: function (action, passes) {
			return new Promise(function (resolve, reject) {
				//	let passes = [];
				let _resp = [];
				let _done = _.after(passes.length, function () {
					resolve(_resp);
				});

				let _doBatch = function (_pass) {
					switch (action) {
						case 'create':
							createPass(_pass, function (err, p) {
								if (err) {
									console.error(err);
									_done();
								}
								_resp.push(p);
								_done();
							});
							break;
						case 'sign':
							signPass(_pass, function (err, p) {
								if (err) {
									console.error(err);
									_done();
								}
								_resp.push(p);
								_done();
							});
							break;
						case 'validate':
							validatePass(_pass, function (err, p) {
								if (err) {
									console.error(err);
									_done();
								}
								_resp.push(p);
								_done();
							});
							break;
						default:

							break;
					}
				};

				_.forEach(passes, function (_pass) {
					logger('batch', action, _pass);
					_.defer(function () {
						_doBatch(_pass);
					});
				});
			});
		},
		createPassPromise: function (pass) {
			return new Promise(function (resolve, reject) {
				createPass(pass, function (err, resp) {
					if (err) {
						reject(err);
					}
					resolve(resp);
				});
			});
		},
		signPassPromise: function (pass) {
			return new Promise(function (resolve, reject) {
				signPass(pass, function (err, resp) {
					if (err) {
						reject(err);
					}
					resolve(resp);
				});
			});
		},
		validatePassPromise: function (pass) {
			return new Promise(function (resolve, reject) {
				validatePass(pass, function (err, resp) {
					if (err) {
						reject(err);
					}
					resolve(resp);
				});
			});
		}
	};
};

